mapper.insert:
INSERT INTO t_order (
	uid, receiver, phone, address, total_price, state, order_time, pay_time,
	created_user, created_time, modified_user, modified_time
) VALUES (
	#{uid}, #{receiver}, #{phone}, #{address}, #{totalPrice}, #{state}, #{orderTime},
	#{payTime}, #{createdUser}, #{createdTime}, #{modifiedUser}, #{modifiedTime}
);

INSERT INTO t_order_item (
	oid, goods_id, goods_title, goods_image, goods_price, goods_num, created_user,
	created_time, modified_user, modified_time
) VALUES (
	#{oid}, #{goodsId}, #{goodsTitle}, #{goodsImage}, #{goodsPrice}, #{goodsNum},
	#{createdUser}, #{createdTime}, #{modifiedUser}, #{modifiedTime}
);
